export const actions=e=>new Promise((async(e,t)=>{e({click:async e=>{const t=e.target.closest("fieldset");if("LABEL"===e.target.tagName)switch(t.dataset.field){case"reset_graph":document.dispatchEvent(new CustomEvent("reset_graph",{bubbles:!0,composed:!0,detail:{value:e.target.textContent.toLowerCase()}}));break;case"goniometer":document.dispatchEvent(new CustomEvent("goniometer",{bubbles:!0,composed:!0,detail:{value:e.target.textContent.toLowerCase()}}));break;case"settingsDelay":document.dispatchEvent(new CustomEvent("settingsDelay",{bubbles:!0,composed:!0,detail:{value:e.target.textContent.toLowerCase()}}));break;case"settingsInput":document.dispatchEvent(new CustomEvent("settingsInput",{bubbles:!0,composed:!0,detail:{value:e.target.textContent.toLowerCase()}}));break;case"LCR":document.dispatchEvent(new CustomEvent("channels",{bubbles:!0,composed:!0,detail:{value:e.target.textContent.toLowerCase()}}));break;default:console.warn("не обрабатывается data-field",t.dataset.field)}}})}));export default{description:"action"};